function [ res ] = uAnalytical( xMat, yMat, mju, u1Zero, u2Zero )
    syms u1(x);
    syms u2(y);
    f1 = @(x) 3.*(x.^3).*exp(x).*cos(x);
    f2 = @(y)  + y.*sin(4.*y) - cos(y);
    part1 = matlabFunction((exp(mju^(1/2)*(x + 1))*(u1Zero - u1Zero*exp(-mju^(1/2)) - (3*exp(1)*(48*exp(mju^(1/2) - 1) - 32*cos(1) + (8*cos(1) + 8*sin(1) - 24*exp(mju^(1/2) - 1))/mju^(1/2) + mju^(3/2)*(26*cos(1) + 6*sin(1) + 6*exp(mju^(1/2) - 1)) + mju^(1/2)*(24*cos(1) - 12*sin(1)) - 12*mju^2*cos(1) - mju^3*cos(1) - mju*(20*cos(1) + 8*sin(1) + 24*exp(mju^(1/2) - 1)) + mju^(5/2)*(4*cos(1) + sin(1))))/(2*(mju - 2*mju^(1/2) + 2)^4) + (3*exp(-mju^(1/2))*((6*mju^2 + 24*mju^(3/2) - 24)/(mju + 2*mju^(1/2) + 2)^4 - (48*mju^(1/2))/(mju + 2*mju^(1/2) + 2)^4 + (exp(mju^(1/2) + 1)*(8*cos(1) + 8*sin(1) + mju^(3/2)*(20*cos(1) + 8*sin(1)) + mju^2*(26*cos(1) + 6*sin(1)) + 12*mju^(5/2)*cos(1) + mju^(7/2)*cos(1) + mju*(24*cos(1) - 12*sin(1)) + mju^3*(4*cos(1) + sin(1))))/(mju + 2*mju^(1/2) + 2)^4 + (32*mju^(1/2)*cos(1)*exp(mju^(1/2) + 1))/(mju + 2*mju^(1/2) + 2)^4))/(2*mju^(1/2))))/(exp(2*mju^(1/2)) - 1) - (exp(-mju^(1/2)*(x - 1))*(u1Zero - u1Zero*exp(mju^(1/2)) - (3*exp(1)*(48*exp(mju^(1/2) - 1) - 32*cos(1) + (8*cos(1) + 8*sin(1) - 24*exp(mju^(1/2) - 1))/mju^(1/2) + mju^(3/2)*(26*cos(1) + 6*sin(1) + 6*exp(mju^(1/2) - 1)) + mju^(1/2)*(24*cos(1) - 12*sin(1)) - 12*mju^2*cos(1) - mju^3*cos(1) - mju*(20*cos(1) + 8*sin(1) + 24*exp(mju^(1/2) - 1)) + mju^(5/2)*(4*cos(1) + sin(1))))/(2*(mju - 2*mju^(1/2) + 2)^4) + (3*exp(-mju^(1/2))*((6*mju^2 + 24*mju^(3/2) - 24)/(mju + 2*mju^(1/2) + 2)^4 - (48*mju^(1/2))/(mju + 2*mju^(1/2) + 2)^4 + (exp(mju^(1/2) + 1)*(8*cos(1) + 8*sin(1) + mju^(3/2)*(20*cos(1) + 8*sin(1)) + mju^2*(26*cos(1) + 6*sin(1)) + 12*mju^(5/2)*cos(1) + mju^(7/2)*cos(1) + mju*(24*cos(1) - 12*sin(1)) + mju^3*(4*cos(1) + sin(1))))/(mju + 2*mju^(1/2) + 2)^4 + (32*mju^(1/2)*cos(1)*exp(mju^(1/2) + 1))/(mju + 2*mju^(1/2) + 2)^4))/(2*mju^(1/2))))/(exp(2*mju^(1/2)) - 1) + (3*exp(mju^(1/2)*x)*int(y^3*exp(-y*(mju^(1/2) - 1))*cos(y), y, 0, x))/(2*mju^(1/2)) - (3*exp(-mju^(1/2)*x)*int(y^3*exp(y*(mju^(1/2) + 1))*cos(y), y, 0, x))/(2*mju^(1/2)));
    part2 =  matlabFunction(exp(-mju^(1/2)*y)*((exp(mju^(1/2)*y)*cos(y))/(2*(mju + 1)) - (4*cos(4*y)*exp(mju^(1/2)*y))/(mju + 16)^2 + (exp(mju^(1/2)*y)*sin(y))/(2*mju^(1/2)*(mju + 1)) - (y*sin(4*y)*exp(mju^(1/2)*y))/(2*(mju + 16)) + (2*y*cos(4*y)*exp(mju^(1/2)*y))/(mju^(1/2)*(mju + 16)) + (sin(4*y)*exp(mju^(1/2)*y)*(mju - 16))/(2*mju^(1/2)*(mju + 16)^2)) - exp(mju^(1/2)*y)*((4*cos(4*y)*exp(-mju^(1/2)*y))/(mju + 16)^2 - (exp(-mju^(1/2)*y)*cos(y))/(2*(mju + 1)) + (exp(-mju^(1/2)*y)*sin(y))/(2*mju^(1/2)*(mju + 1)) + (y*sin(4*y)*exp(-mju^(1/2)*y))/(2*(mju + 16)) + (2*y*cos(4*y)*exp(-mju^(1/2)*y))/(mju^(1/2)*(mju + 16)) + (sin(4*y)*exp(-mju^(1/2)*y)*(mju - 16))/(2*mju^(1/2)*(mju + 16)^2)) + (exp(mju^(1/2)*(y + 1))*(256*u2Zero - 256*cos(1) + 8*cos(4) + 16*sin(4) + 248*exp(-mju^(1/2)) - 32*mju*cos(1) + 8*mju*cos(4) + 17*mju*sin(4) + 288*mju*u2Zero + 24*mju*exp(-mju^(1/2)) - 256*u2Zero*exp(-mju^(1/2)) - mju^2*cos(1) + mju^2*sin(4) + 33*mju^2*u2Zero + mju^3*u2Zero + mju^2*exp(-mju^(1/2)) - 33*mju^2*u2Zero*exp(-mju^(1/2)) - mju^3*u2Zero*exp(-mju^(1/2)) - 288*mju*u2Zero*exp(-mju^(1/2))))/((mju + 1)*(mju + 16)^2*(exp(2*mju^(1/2)) - 1)) - (exp(-mju^(1/2)*(y - 1))*(256*u2Zero + 248*exp(mju^(1/2)) - 256*cos(1) + 8*cos(4) + 16*sin(4) - 256*u2Zero*exp(mju^(1/2)) - 32*mju*cos(1) + 8*mju*cos(4) + 17*mju*sin(4) + 288*mju*u2Zero + mju^2*exp(mju^(1/2)) - mju^2*cos(1) + mju^2*sin(4) + 33*mju^2*u2Zero + mju^3*u2Zero + 24*mju*exp(mju^(1/2)) - 288*mju*u2Zero*exp(mju^(1/2)) - 33*mju^2*u2Zero*exp(mju^(1/2)) - mju^3*u2Zero*exp(mju^(1/2))))/((mju + 1)*(mju + 16)^2*(exp(2*mju^(1/2)) - 1)));
    
%     part1 = matlabFunction(dsolve(diff(u1, 2) - mju*u1 == f1, ...
%         u1(0) == u1Zero, u1(1) == u1Zero));
%     part2 = matlabFunction(dsolve(diff(u2, 2) - mju*u2 == f2, ...
%         u2(0) == u2Zero, u2(1) == u2Zero));
    res = part1(xMat) + part2(yMat);
end

